def is_metadata_file(file_path):
    """Check if file is metadata related"""
    metadata_indicators = [
        'metadata.txt',
        'readme.txt'
    ]
    return any(indicator in file_path.lower() for indicator in metadata_indicators)

def should_process_file(file_path):
    """Check if file should be processed (CSV or Excel) - case insensitive"""
    extensions = ['.csv', '.xlsx', '.xls']
    return any(file_path.lower().endswith(ext) for ext in extensions)

def get_file_type(file_path):
    """Determine if file is CSV or Excel"""
    if file_path.lower().endswith(('.xlsx', '.xls')):
        return 'excel'
    elif file_path.lower().endswith('.csv'):
        return 'csv'
    return None

def is_zip_file(file_path):
    """Check if file is a zip file"""
    return file_path.lower().endswith('.zip')

def is_empty_file(file_path):
    """Check if a file is empty or very small with improved reliability"""
    try:
        # First check if file exists
        if not mssparkutils.fs.exists(file_path):
            print(f"File doesn't exist, can't check if empty: {file_path}")
            return False

        # For CSV files, use a more direct approach
        if file_path.lower().endswith('.csv'):
            # Try reading the first few lines directly
            try:
                text_df = spark.read.text(file_path).limit(5)
                count = text_df.count()

                # If no rows or only header
                if count == 0:
                    return True
                if count == 1:
                    # Check if it's just a header line
                    first_row = text_df.first()[0]
                    if len(first_row.strip()) < 10:
                        return True
                return False
            except:
                # Fall back to CSV reader with header option
                try:
                    csv_df = spark.read.option("header", "true").csv(file_path).limit(5)
                    return csv_df.count() == 0
                except:
                    # If both approaches fail, assume it's not an empty file
                    return False

        # For Excel files, check differently
        elif file_path.lower().endswith(('.xlsx', '.xls')):
            try:
                # Just check if we can read any rows
                excel_df = spark.read.format("com.crealytics.spark.excel").option("header", "true").load(file_path).limit(5)
                return excel_df.count() == 0
            except:
                # If read fails, don't assume it's empty
                return False

        # Default approach for other file types
        try:
            # Use a basic text read as fallback
            test_df = spark.read.text(file_path).limit(5)
            return test_df.count() == 0
        except Exception as e:
            print(f"Error checking if file is empty: {str(e)}")
            return False
    except Exception as e:
        print(f"Error in is_empty_file check: {str(e)}")
        # Default to false if we can't determine
        return False

def unzip_file(file_path, extract_to, storage_account):
    """Unzip a file to the specified directory with improved error handling"""
    try:
        print(f"Attempting to unzip: {file_path}")

        # Create a temporary local directory
        with tempfile.TemporaryDirectory() as tmpdirname:
            local_zip_path = os.path.join(tmpdirname, os.path.basename(file_path))

            # Download the zip file to the local temporary directory
            print(f"Downloading zip file from {file_path} to {local_zip_path}")
            mssparkutils.fs.cp(file_path, f"file://{local_zip_path}")

            # Check if zip file is password protected before trying to extract
            try:
                zip_file = zipfile.ZipFile(local_zip_path)
                # Try to read the first file to see if it's password protected
                for zip_info in zip_file.infolist():
                    if zip_info.flag_bits & 0x1:
                        print(f"ZIP file is encrypted/password protected: {file_path}")
                        return False, "password_protected"

                    # Try to read a little bit of data to check for password
                    try:
                        zip_file.open(zip_info.filename).read(1)
                    except RuntimeError as e:
                        if 'password required' in str(e).lower() or 'bad password' in str(e).lower():
                            print(f"Password required for ZIP file: {file_path}")
                            return False, "password_protected"
                        raise

                # If we got here, try to extract
                print(f"Extracting ZIP file to {extract_to}")
                zip_file.extractall(path=extract_to)
                zip_file.close()

            except zipfile.BadZipFile as e:
                print(f"Bad ZIP file: {file_path} - {str(e)}")
                return False, "bad_zip_file"
            except RuntimeError as e:
                error_msg = str(e).lower()
                if 'password required' in error_msg or 'encrypted' in error_msg or 'bad password' in error_msg:
                    print(f"Password protected ZIP file detected: {file_path}")
                    return False, "password_protected"
                raise

        # If extraction succeeded, upload the extracted files back to ADLS
        print(f"Uploading extracted files to Azure Data Lake")
        for root, dirs, files in os.walk(extract_to):
            for file in files:
                local_file_path = os.path.join(root, file)
                relative_path = os.path.relpath(local_file_path, extract_to)
                remote_dir = os.path.dirname(file_path)
                remote_file_path = f"{remote_dir}/{relative_path}"

                print(f"Uploading: {local_file_path} to {remote_file_path}")
                mssparkutils.fs.cp(f"file://{local_file_path}", remote_file_path)

        print(f"Successfully unzipped file: {file_path}")
        return True, None

    except RuntimeError as e:
        error_msg = str(e).lower()
        if 'password required' in error_msg or 'encrypted' in error_msg or 'bad password' in error_msg:
            print(f"Password protected ZIP file detected: {file_path}")
            return False, "password_protected"
        else:
            print(f"Runtime error unzipping file: {str(e)}")
            return False, str(e)
    except Exception as e:
        print(f"Error unzipping file: {file_path} - {str(e)}")
        return False, str(e)

def process_file_group(file_group, date_folder, la_folder, storage_account, moved_files):
    """Process a group of files with the same date and LA folder with better coordination"""
    try:
        print(f"\nProcessing file group: date={date_folder}, LA={la_folder}, files={len(file_group)}")

        # Create the folder name in the format YYYYMMDD_LA-Name
        folder_key = f"{date_folder}_{la_folder}"

        # Ensure the destination directory exists
        dest_dir = f"abfss://juror-etl@{storage_account}.dfs.core.windows.net/files"
        if not mssparkutils.fs.exists(dest_dir):
            print(f"Creating destination directory: {dest_dir}")
            mssparkutils.fs.mkdirs(dest_dir)

        # Check which files still exist and get their types
        valid_files = []
        for info in file_group:
            path = info["path"]
            if mssparkutils.fs.exists(path):
                valid_files.append(info)
            else:
                print(f"  File doesn't exist, skipping from group: {path}")

        if not valid_files:
            print("  No valid files remain in this group")
            return False, None

        # Group files by type for easier processing
        csv_files = [info for info in valid_files if info["file_type"] == "csv"]
        excel_files = [info for info in valid_files if info["file_type"] == "excel"]

        # Process CSV files if present
        if csv_files:
            csv_dest_path = f"{dest_dir}/{folder_key}.csv"
            print(f"  Processing {len(csv_files)} CSV files to: {csv_dest_path}")

            try:
                # Read and merge all CSV files
                if len(csv_files) == 1:
                    # Just copy the single file
                    path = csv_files[0]["path"]
                    mssparkutils.fs.cp(path, csv_dest_path, True)
                    print(f"  Copied single CSV file to {csv_dest_path}")

                    # Try to delete original
                    try:
                        mssparkutils.fs.rm(path)
                    except Exception as e:
                        print(f"  Warning: Could not delete original file: {str(e)}")

                    for path in [csv_files[0]["path"]]:
                        moved_files['data_success'].append(csv_dest_path)
                else:
                    # Merge multiple files
                    dfs = []
                    for info in csv_files:
                        try:
                            df = spark.read.option("header", "true").csv(info["path"])
                            dfs.append(df)
                        except Exception as e:
                            print(f"  Error reading CSV file {info['path']}: {str(e)}")
                            moved_files['failed'].append(f"{info['path']} (CSV read failed)")

                    if dfs:
                        # Merge the dataframes
                        merged_df = dfs[0]
                        for df in dfs[1:]:
                            merged_df = merged_df.unionByName(df, allowMissingColumns=True)

                        # Write the merged dataframe
                        merged_df.write.mode("overwrite").option("header", "true").csv(csv_dest_path)
                        print(f"  Successfully merged {len(dfs)} CSV files to {csv_dest_path}")

                        # Try to delete originals
                        for info in csv_files:
                            try:
                                if mssparkutils.fs.exists(info["path"]):
                                    mssparkutils.fs.rm(info["path"])
                            except Exception as e:
                                print(f"  Warning: Could not delete original file: {str(e)}")

                        # Update success tracking
                        for info in csv_files:
                            moved_files['data_success'].append(csv_dest_path)
            except Exception as e:
                print(f"  Error processing CSV group: {str(e)}")
                import traceback
                print(f"  Traceback: {traceback.format_exc()}")
                for info in csv_files:
                    moved_files['failed'].append(f"{info['path']} (CSV group processing failed)")

        # Process Excel files (just take the first one as you can't easily merge Excel files)
        if excel_files:
            excel_dest_path = f"{dest_dir}/{folder_key}.xlsx"
            print(f"  Processing Excel files, using first of {len(excel_files)} to: {excel_dest_path}")

            try:
                # Just use the first Excel file
                path = excel_files[0]["path"]
                mssparkutils.fs.cp(path, excel_dest_path, True)
                print(f"  Copied first Excel file to {excel_dest_path}")

                # Try to delete original
                try:
                    mssparkutils.fs.rm(path)
                except Exception as e:
                    print(f"  Warning: Could not delete original file: {str(e)}")

                # Update success tracking for first file
                moved_files['data_success'].append(excel_dest_path)

                # Process additional Excel files individually
                if len(excel_files) > 1:
                    print(f"  Processing remaining {len(excel_files)-1} Excel files individually")
                    for i in range(1, len(excel_files)):
                        process_individual_file(excel_files[i], storage_account, moved_files)
            except Exception as e:
                print(f"  Error processing Excel files: {str(e)}")
                import traceback
                print(f"  Traceback: {traceback.format_exc()}")
                for info in excel_files:
                    moved_files['failed'].append(f"{info['path']} (Excel processing failed)")

        return True, None

    except Exception as e:
        print(f"Error processing file group: {str(e)}")
        import traceback
        print(f"Traceback: {traceback.format_exc()}")
        return False, None

def print_detailed_summary(moved_files):
    """Print detailed summary of moved files"""
    print("\nSummary of Moved Files:")
    print(f"Data Files Successfully Moved: {len(moved_files['data_success'])}")
    for file in moved_files['data_success']:
        print(f"  - {file}")
    print(f"Metadata Files Successfully Moved: {len(moved_files['metadata_success'])}")
    for file in moved_files['metadata_success']:
        print(f"  - {file}")
    print(f"Failed Files: {len(moved_files['failed'])}")
    for file in moved_files['failed']:
        print(f"  - {file}")
    print(f"Quarantined Files: {len(moved_files['quarantine'])}")
    for file in moved_files['quarantine']:
        print(f"  - {file}")
