def is_metadata_file(file_path):
    """Check if file is metadata related"""
    metadata_indicators = [
        'metadata.txt',
        'ReadMe.txt'
    ]
    return any(indicator in file_path.lower() for indicator in metadata_indicators)

def process_folder(path):
    """Process a folder recursively and return all files"""
    all_files = []
    try:
        files = mssparkutils.fs.ls(path)
        for file_info in files:
            if file_info.isDir:
                sub_files = process_folder(file_info.path)
                all_files.extend(sub_files)
            else:
                if 'File Structure Definitions.xlsx' not in file_info.path:
                    all_files.append(file_info.path)
    except Exception as e:
        print(f"Error accessing path {path}: {str(e)}")
    return all_files

def get_folder_parts(file_path):
    """Extract folder information for naming"""
    parts = file_path.split('/')
    date_folder = next((p for p in parts if p.startswith('20')), None)

    if date_folder and 'LA_Data' in parts:
        la_index = parts.index('LA_Data')
        if len(parts) > la_index + 1:
            third_folder = parts[la_index + 1]
            return date_folder, third_folder
    return None, None

def clean_file_name(file_name):
    """Clean file name"""
    if '?' in file_name:
        file_name = file_name.split('?')[0]
    return file_name.replace(' ', '_').replace('&', 'and')

def move_file(source_path, dest_path):
    """Move file using mssparkutils"""
    try:
        print(f"Moving {source_path} to {dest_path}")
        mssparkutils.fs.cp(source_path, dest_path, True)
        return True
    except Exception as e:
        print(f"Warning - Error moving file: {str(e)}")
        return False

def process_csv_file(source_path, date_folder, third_folder, file_name):
    """Process CSV file with folder structure"""
    try:
        base_dir = f"{date_folder}_{third_folder}"
        dest_path = f"abfss://juror-etl@{storage_account}.dfs.core.windows.net/csv/{base_dir}/{clean_file_name(file_name)}"
        return move_file(source_path, dest_path), f"{base_dir}/{clean_file_name(file_name)}"
    except Exception as e:
        print(f"Warning - Error processing CSV file {source_path}: {str(e)}")
        return False, None

def process_metadata_file(source_path, file_name):
    """Process metadata file"""
    try:
        dest_path = f"abfss://juror-etl@{storage_account}.dfs.core.windows.net/metadata/{clean_file_name(file_name)}"
        return move_file(source_path, dest_path), clean_file_name(file_name)
    except Exception as e:
        print(f"Warning - Error processing metadata file {source_path}: {str(e)}")
        return False, None

def print_summary(moved_files):
    """Print summary of file movements"""
    print("\n=== File Movement Summary ===")

    print(f"\nSuccessfully Moved CSV Files:")
    print("-" * 100)
    for file in moved_files['csv_success']:
        print(f"✓ {file}")

    print(f"\nSuccessfully Moved Metadata Files:")
    print("-" * 100)
    for file in moved_files['metadata_success']:
        print(f"✓ {file}")

    print(f"\nFailed Files:")
    print("-" * 100)
    for file in moved_files['failed']:
        print(f"✗ {file}")

    print("\nSummary Counts:")
    print("-" * 100)
    print(f"CSV Files Moved: {len(moved_files['csv_success'])}")
    print(f"Metadata Files Moved: {len(moved_files['metadata_success'])}")
    print(f"Failed Files: {len(moved_files['failed'])}")
    print(f"Total Processed: {len(moved_files['csv_success']) + len(moved_files['metadata_success']) + len(moved_files['failed'])}")

# Main processing
try:
    storage_account = "baubaisadfsastg"
    source_path = f"abfss://juror-ingestion@{storage_account}.dfs.core.windows.net"

    # Track moved files
    moved_files = {
        'csv_success': [],
        'metadata_success': [],
        'failed': []
    }

    print(f"Scanning source path: {source_path}")
    files = process_folder(source_path)
    print(f"Found {len(files)} files to process")

    for file_path in files:
        try:
            file_name = file_path.split('/')[-1]

            if is_metadata_file(file_path):
                success, new_path = process_metadata_file(file_path, file_name)
                if success:
                    moved_files['metadata_success'].append(f"{file_path} -> {new_path}")
                else:
                    moved_files['failed'].append(f"{file_path} (metadata move failed)")
                continue

            if not file_path.lower().endswith('.csv'):
                continue

            date_folder, third_folder = get_folder_parts(file_path)
            if date_folder and third_folder:
                success, new_path = process_csv_file(file_path, date_folder, third_folder, file_name)
                if success:
                    moved_files['csv_success'].append(f"{file_path} -> {new_path}")
                else:
                    moved_files['failed'].append(f"{file_path} (csv move failed)")
            else:
                moved_files['failed'].append(f"{file_path} (invalid folder structure)")

        except Exception as e:
            print(f"Warning - Error processing {file_path}: {str(e)}")
            moved_files['failed'].append(f"{file_path} (processing error)")
            continue

except Exception as e:
    print(f"Warning - Error in main processing: {str(e)}")

# Print final summary
print_summary(moved_files)
