def cast_to_target_schema(df):
    """Force DataFrame to match the TARGET_SCHEMA types"""
    from pyspark.sql.functions import col

    schema_dict = dict(TARGET_SCHEMA)

    for column_name, data_type in schema_dict.items():
        if column_name in df.columns:
            df = df.withColumn(column_name, col(column_name).cast(data_type))

    return df

def read_column_mapping(spark, config_path):
    """Read and parse the column mapping configuration"""
    try:
        # Read the JSON file
        mapping_df = spark.read.text(config_path)

        # Combine all lines into a single string
        json_str = mapping_df.agg(concat_ws("", collect_list("value"))).collect()[0][0]

        # Parse the JSON string
        mapping_dict = json.loads(json_str)

        # Check if it has 'schema' structure
        if "schema" in mapping_dict:
            # Convert schema format to mappings format
            mappings = {}
            for item in mapping_dict["schema"]:
                # Use the 'name' field as the standard column name
                # Initially set empty variations list
                mappings[item["name"]] = []

            return mappings

        # If looking for mappings specifically
        elif "mappings" in mapping_dict:
            return mapping_dict["mappings"]

        # If neither structure is found, raise an error
        else:
            raise KeyError("Neither 'schema' nor 'mappings' found in mapping file")

    except Exception as e:
        try:
            # Fallback to using wholeTextFiles
            mapping_rdd = spark.sparkContext.wholeTextFiles(config_path)
            json_str = mapping_rdd.values().first()
            mapping_dict = json.loads(json_str)

            # Check the structure here too
            if "schema" in mapping_dict:
                mappings = {}
                for item in mapping_dict["schema"]:
                    mappings[item["name"]] = []
                return mappings
            elif "mappings" in mapping_dict:
                return mapping_dict["mappings"]
            else:
                raise KeyError("Neither 'schema' nor 'mappings' found in mapping file")

        except Exception as e2:
            raise Exception(f"Failed to read mapping file. Primary error: {str(e)}, Fallback error: {str(e2)}")

def read_schema_config(spark, config_path):
    """Read and parse the schema configuration"""
    try:
        schema_df = spark.read.text(config_path)
        json_str = schema_df.agg(concat_ws("", collect_list("value"))).collect()[0][0]
        schema_dict = json.loads(json_str)
        return schema_dict["schema"]
    except Exception as e:
        try:
            schema_rdd = spark.sparkContext.wholeTextFiles(config_path)
            json_str = schema_rdd.values().first()
            schema_dict = json.loads(json_str)
            return schema_dict["schema"]
        except Exception as e2:
            raise Exception(f"Failed to read schema file. Primary error: {str(e)}, Fallback error: {str(e2)}")

def ensure_consistent_schema(df, required_columns):
    """Ensure DataFrame has all required columns with correct data types"""
    current_columns = set(df.columns)

    # Add missing columns
    for col_name, col_type in required_columns:
        if col_name not in current_columns:
            df = df.withColumn(col_name, lit(None).cast(col_type))

    # Select only the required columns in the specified order
    return df.select([col(c[0]).cast(c[1]) for c in required_columns])

def get_new_files(spark, input_path, process_log_path):
    """Identify which files are new and need processing with improved error handling"""
    from notebookutils import mssparkutils

    # Override process_log_path to use our specific L2 text log file
    l2_log_path = f"abfss://juror-etl@baubaisadfsaprod.dfs.core.windows.net/l2_process_logs/processed_files_log.txt"

    # Get all files in the input directory
    all_files = []

    # List all items in the directory
    try:
        items = mssparkutils.fs.ls(input_path)
        print(f"Found {len(items)} items in {input_path}")

        for item in items:
            if (hasattr(item, 'path') and (
                   item.path.endswith(".parquet") or 
                   (hasattr(item, 'isDir') and item.isDir and 
                    mssparkutils.fs.exists(f"{item.path}/_SUCCESS"))
               )):
                all_files.append(item.path)
                print(f"Added file/directory for processing: {item.path}")
    except Exception as e:
        print(f"Error listing files in {input_path}: {str(e)}")

    # Read process log to get previously processed files
    processed_files = []
    if mssparkutils.fs.exists(l2_log_path):
        try:
            # Read as plain text file using direct file system API
            print(f"Reading process log from {l2_log_path}")
            content = mssparkutils.fs.read(l2_log_path)

            # Process each line
            for line in content.split("\n"):
                if line.strip():
                    # Extract just the file path (before the | symbol if it exists)
                    if "|" in line:
                        file_path = line.split("|")[0].strip()
                    else:
                        file_path = line.strip()
                    processed_files.append(file_path)

            print(f"Read {len(processed_files)} processed files from log")
        except Exception as e:
            print(f"Error reading process log: {str(e)}")
            print("Treating all files as new since process log couldn't be read")
    else:
        print(f"Process log doesn't exist at {l2_log_path}, treating all files as new")

    # Identify new files with more detailed logging
    new_files = []
    for f in all_files:
        if f not in processed_files:
            new_files.append(f)
            print(f"New file to process: {f}")
        else:
            print(f"Skipping already processed file: {f}")

    print(f"Found {len(new_files)} new files to process out of {len(all_files)} total files")
    return new_files, all_files
