def is_metadata_file(file_path):
    """Check if file is metadata related"""
    metadata_indicators = [
        'metadata.txt',
        'ReadMe.txt'
    ]
    return any(indicator in file_path for indicator in metadata_indicators)

def process_folder(path):
    """Process a folder recursively and return all file paths"""
    all_files = []
    try:
        files = mssparkutils.fs.ls(path)
        for file_info in files:
            if file_info.isDir:
                sub_files = process_folder(file_info.path)
                all_files.extend(sub_files)
            else:
                if not 'File Structure Definitions.xlsx' in file_info.path:
                    all_files.append(file_info.path)
    except Exception as e:
        print(f"Error accessing path {path}: {str(e)}")
    return all_files

def get_folder_parts(file_path):
    """Extract folder information for naming"""
    parts = file_path.split('/')

    # Get the main date folder (starts with '20')
    date_folder = next((p for p in parts if p.startswith('20')), None)

    if date_folder and 'LA_Data' in parts:
        date_index = parts.index(date_folder)
        la_index = parts.index('LA_Data')

        # Get the third folder (the one after LA_Data)
        if len(parts) > la_index + 1:
            third_folder = parts[la_index + 1]
            return date_folder, third_folder

    return None, None

def clean_file_name(file_name):
    """Clean and standardize file names"""
    # Remove query parameters
    if '?' in file_name:
        file_name = file_name.split('?')[0]

    # Replace problematic characters
    return file_name.replace(' ', '_').replace('&', 'and')

def move_file(source_path, dest_path):
    """Move file using mssparkutils"""
    try:
        print(f"Moving {source_path} to {dest_path}")
        mssparkutils.fs.cp(source_path, dest_path, True)
        return True
    except Exception as e:
        print(f"Error moving file: {str(e)}")
        return False

def process_csv_file(source_path, date_folder, third_folder, file_name):
    """Process CSV file with simplified naming (datefolder_thirdfolder)"""
    try:
        # Create the base directory name using just date and third folder
        base_dir = f"{date_folder}_{third_folder}"

        # Keep original file name for the file itself
        dest_path = f"abfss://juror-etl@{storage_account}.dfs.core.windows.net/csv/{base_dir}/{clean_file_name(file_name)}"

        print(f"Moving CSV file to {dest_path}")
        return move_file(source_path, dest_path), f"{base_dir}/{clean_file_name(file_name)}"
    except Exception as e:
        print(f"Error processing CSV file {source_path}: {str(e)}")
        return False, None

def print_summary(moved_files):
    """Print detailed summary of file movements"""
    print("\n=== File Movement Summary ===")

    total_files = 0
    for category in ['csv', 'metadata', 'quarantine']:
        print(f"\n{category.upper()} Files:")
        print("-" * 100)
        files = moved_files.get(category, [])
        for file in files:
            print(f"âœ“ {file}")
        print(f"\n{category.capitalize()} Count: {len(files)}")
        total_files += len(files)

    print("\nSummary Counts:")
    print("-" * 100)
    for category in ['csv', 'metadata', 'quarantine']:
        count = len(moved_files.get(category, []))
        print(f"{category.capitalize()} Files: {count}")
    print(f"Total Files Processed: {total_files}")

# Main processing
try:
    storage_account = "baubaisadfsastg"
    source_path = f"abfss://juror-ingestion@{storage_account}.dfs.core.windows.net"

    # Track moved files
    moved_files = {
        'csv': [],
        'metadata': [],
        'quarantine': []
    }

    print(f"Scanning source path: {source_path}")
    files = process_folder(source_path)
    print(f"Found {len(files)} files to process")

    for file_path in files:
        try:
            file_name = file_path.split('/')[-1]
            date_folder, third_folder = get_folder_parts(file_path)

            if not date_folder or not third_folder:
                moved_files['quarantine'].append(f"{file_path} (invalid folder structure)")
                continue

            if is_metadata_file(file_path):
                # Move metadata files directly to metadata folder
                dest_path = f"abfss://name-output@{storage_account}.dfs.core.windows.net/metadata/{file_name}"
                if move_file(file_path, dest_path):
                    moved_files['metadata'].append(f"{file_path} -> {file_name}")
                else:
                    moved_files['quarantine'].append(f"{file_path} (metadata move failed)")

            elif file_path.lower().endswith('.csv'):
                success, new_path = process_csv_file(file_path, date_folder, third_folder, file_name)
                if success:
                    moved_files['csv'].append(f"{file_path} -> {new_path}")
                else:
                    moved_files['quarantine'].append(f"{file_path} (csv processing failed)")
            else:
                moved_files['quarantine'].append(f"{file_path} (unsupported file type)")

        except Exception as e:
            print(f"Error processing {file_path}: {str(e)}")
            moved_files['quarantine'].append(f"{file_path} (processing error)")

except Exception as e:
    print(f"Error in main processing: {str(e)}")

# Print final summary
print_summary(moved_files)
